+THLongTensor *THSTensor_(indices)(const THSTensor *self) {
+  if (self->nnz == 0) {
+    // Narrows don't work on 0-length tensors
+    THLongTensor_retain(self->indices);
+    return self->indices;
+  }
+  return THLongTensor_newNarrow(self->indices, 1, 0, self->nnz);
+}
