+{
+  HANDLE_TH_ERRORS
+  THPCppFunction* self = (THPCppFunction*)obj;
+  auto& val = (T*)(self->cdata.get())->*ptr;
+  return Convert(val);
+  END_HANDLE_TH_ERRORS
+}
