+DimVector TensorIterator::compatible_stride(int element_size) const {
+  auto stride = DimVector();
+  stride.push_back(element_size);
+  for (int i = 0; i < ndim() - 1; i++) {
+    stride.push_back(shape_[i] * stride[i]);
+  }
+  return stride;
+}
