+__global__ void AdadeltaUpdate(
+    int N,
+    const float* w,
+    const float* g,
+    const float* h,
+    const float* d,
+    float* nw,
+    float* nh,
+    float* nd,
+    float epsilon,
+    float decay,
+    const float* lr) {
+  CUDA_1D_KERNEL_LOOP(i, N) {
+    float gi = g[i];
+    float di = d[i];
+    float hi = nh[i] = decay * h[i] + (1.0f - decay) * gi * gi;
+    float ng = (std::sqrt(di + epsilon) / std::sqrt(hi + epsilon)) * gi;
+    nw[i] = w[i] + lr[0] * ng;
+    nd[i] = decay * di + (1.0f - decay) * ng * ng;
+  }
+}
