+template <>
+void adadelta_update<CUDAContext>(
+    int N,
+    const float* w,
+    const float* g,
+    const float* h,
+    const float* d,
+    float* nw,
+    float* nh,
+    float* nd,
+    float epsilon,
+    float decay,
+    const float* lr,
+    CUDAContext* context) {
+  AdadeltaUpdate<<<
+      CAFFE_GET_BLOCKS(N),
+      CAFFE_CUDA_NUM_THREADS,
+      0,
+      context->cuda_stream()>>>(N, w, g, h, d, nw, nh, nd, epsilon, decay, lr);
+}
