+template <typename DeviceFunc>
+inline Cuda3DLaunchConfig GetCuda3DLaunchConfig(int xdim, int ydim, int zdim,
+                                                const GPUDevice& d, DeviceFunc func,
+                                                size_t dynamic_shared_memory_size = 0) {
+  int block_count = 0;
+  int thread_per_block = 0;
