+    const auto scale = static_cast<T>(1.0507009873554804934193349852946);
+    const auto alpha = static_cast<T>(1.6732632423543772848170429916717);
+    const auto one = static_cast<T>(1);
+    const auto zero = static_cast<T>(0);
