class MyModel(Model):
    def __init__(self):
        super(MyModel, self).__init__()
        # self.layers = []
        for i in range(20):
            setattr(self, "layer%i" % i, MyLayer())

    def call(self, inputs):
        x = inputs[0]
        y = inputs[1]
        for i in range(20):
            x = getattr(self, "layer%i" % i)(x, y)
        return x
